import client from "../../../shared/http-client/Client";

export async function getGenres() {
    const { data: { data } } = await client.get('/genres');

    return data;
}

export async function getSingleGenres(id) {
    const { data: { data } } = await client.get(`/genres/${id}`);

    return data;
}

export async function createGenre(genre) {
    const { data: { data } } = await client.post('/genres', genre)
}

export async function updateGenre(genre) {
    const { data: { data } } = await client.put('/genres', genre);
    return data;
}
